// =============== Switch Function (32 outputs, 2-dp rounding) ===============
// Output map:
//  1  LU CO2 avg        2  LU CO2 raw         3  LU Temp (C)      4  LU RH (%)        5  LU Pressure (hPa)
//  6  RU CO2 avg        7  RU CO2 raw         8  RU Temp (C)      9  RU RH (%)       10  RU Pressure (hPa)
// 11  LD CO2 avg       12  LD CO2 raw        13  LD Temp (C)     14  LD RH (%)       15  LD Pressure (hPa)
// 16  RD CO2 avg       17  RD CO2 raw        18  RD Temp (C)     19  RD RH (%)       20  RD Pressure (hPa)
// 21  LEE600           22  REE600            23  LEE650T (C)     24  REE650T (C)    25  LEE650AV (m/s)
// 26  REE650AV (m/s)   27  LEM watts         28  REM watts        29  LPID block(1..15)  30  RPID block(1..15)
// 31  LVFD (arraysplitter)                 32  RVFD (arraysplitter)

// -------- helpers --------

// E+E float32 values are word-swapped (hi,lo -> lo,hi for bytes)
function readFloat32Swap(r0, r1) {
  const b = Buffer.alloc(4);
  b.writeUInt16BE(r1, 0); // low word
  b.writeUInt16BE(r0, 2); // high word
  return b.readFloatBE(0);
}
function f32at(regs, i) { return readFloat32Swap(regs[i], regs[i + 1]); }

// Generic Int32 (big-endian) from two holding registers
function readInt32BE(r0, r1) {
  const b = Buffer.alloc(4);
  b.writeUInt16BE(r0, 0);
  b.writeUInt16BE(r1, 2);
  return b.readInt32BE(0); // signed
}

// SDM230 (energy meter): float32, MS-register first (r0 = high reg, r1 = low reg)
function f32be(r0, r1) {
  const b = Buffer.alloc(4);
  b.writeUInt16BE(r0, 0); // high reg
  b.writeUInt16BE(r1, 2); // low  reg
  return b.readFloatBE(0);
}

// round to 2 decimals
function round2(v) { return Math.round(v * 100) / 100; }

const regs = msg.payload;
const tag = String(msg.topic || "");

if (!Array.isArray(regs)) {
  node.error("Payload is not an array of registers", msg);
  return null;
}

const out = Array.from({ length: 32 }, () => null);

// side helpers
function isLeftUp(t) { return /^LU/i.test(t); }
function isRightUp(t) { return /^RU/i.test(t); }
function isLeftDown(t) { return /^LD/i.test(t); }
function isRightDown(t) { return /^RD/i.test(t); }

// ---------- EE872: CO2 block (avg+raw at 0x424..0x427) ----------
if (/EE872CO2$/i.test(tag)) {
  if (regs.length < 4) { node.error(`${tag} requires 4 registers`, msg); return null; }
  const co2_avg = round2(f32at(regs, 0)); // 0x424..0x425
  const co2_raw = round2(f32at(regs, 2)); // 0x426..0x427

  if (isLeftUp(tag)) { out[0] = { topic: "LU.CO2.avg", payload: co2_avg }; out[1] = { topic: "LU.CO2.raw", payload: co2_raw }; }
  else if (isRightUp(tag)) { out[5] = { topic: "RU.CO2.avg", payload: co2_avg }; out[6] = { topic: "RU.CO2.raw", payload: co2_raw }; }
  else if (isLeftDown(tag)) { out[10] = { topic: "LD.CO2.avg", payload: co2_avg }; out[11] = { topic: "LD.CO2.raw", payload: co2_raw }; }
  else if (isRightDown(tag)) { out[15] = { topic: "RD.CO2.avg", payload: co2_avg }; out[16] = { topic: "RD.CO2.raw", payload: co2_raw }; }
  else node.warn(`Unhandled CO2 tag: ${tag}`);

  return out;
}

// ---------- EE872: TRH wide block (Temp at 0x3EA..0x3EB, RH at 0x3FC..0x3FD) ----------
if (/EE872TRH$/i.test(tag)) {
  if (regs.length < 20) { node.error(`${tag} requires 20 registers`, msg); return null; }
  const temperature_c = round2(f32at(regs, 0));   // 0x3EA..0x3EB
  const rh_percent = round2(f32at(regs, 18));  // 0x3FC..0x3FD

  if (isLeftUp(tag)) { out[2] = { topic: "LU.Temp.C", payload: temperature_c }; out[3] = { topic: "LU.RH.%", payload: rh_percent }; }
  else if (isRightUp(tag)) { out[7] = { topic: "RU.Temp.C", payload: temperature_c }; out[8] = { topic: "RU.RH.%", payload: rh_percent }; }
  else if (isLeftDown(tag)) { out[12] = { topic: "LD.Temp.C", payload: temperature_c }; out[13] = { topic: "LD.RH.%", payload: rh_percent }; }
  else if (isRightDown(tag)) { out[17] = { topic: "RD.Temp.C", payload: temperature_c }; out[18] = { topic: "RD.RH.%", payload: rh_percent }; }
  else node.warn(`Unhandled TRH tag: ${tag}`);

  return out;
}

// ---------- EE872: Pressure (0x4B0..0x4B1) ----------
if (/EE872P$/i.test(tag)) {
  if (regs.length < 2) { node.error(`${tag} requires 2 registers`, msg); return null; }
  const pressure_hpa = round2(f32at(regs, 0));

  if (isLeftUp(tag)) out[4] = { topic: "LU.P.hPa", payload: pressure_hpa };
  else if (isRightUp(tag)) out[9] = { topic: "RU.P.hPa", payload: pressure_hpa };
  else if (isLeftDown(tag)) out[14] = { topic: "LD.P.hPa", payload: pressure_hpa };
  else if (isRightDown(tag)) out[19] = { topic: "RD.P.hPa", payload: pressure_hpa };
  else node.warn(`Unhandled Pressure tag: ${tag}`);

  return out;
}

// ---------- EE600 / EE650 (single float32 swap) ----------
if (tag === "LEE600" || tag === "REE600" || tag === "LEE650T" || tag === "REE650T" || tag === "LEE650AV" || tag === "REE650AV") {
  if (regs.length < 2) { node.error(`${tag} requires 2 registers`, msg); return null; }
  const val = round2(f32at(regs, 0));

  switch (tag) {
    case "LEE600": out[20] = { topic: "LEE600", payload: val }; break;
    case "REE600": out[21] = { topic: "REE600", payload: val }; break;
    case "LEE650T": out[22] = { topic: "LEE650T.C", payload: val }; break;
    case "REE650T": out[23] = { topic: "REE650T.C", payload: val }; break;
    case "LEE650AV": out[24] = { topic: "LEE650AV.mps", payload: val }; break;
    case "REE650AV": out[25] = { topic: "REE650AV.mps", payload: val }; break;
  }
  return out;
}

// ---------- Energy meters ----------
// SDM230 (LEM/REM): float32 in Input Registers (FC=4), MS-register first
if (tag === "LEM" || tag === "REM") {
  if (regs.length < 2) { node.error(`${tag} requires 2 registers`, msg); return null; }
  const watts = round2(f32be(regs[0], regs[1]));   // Active Power (W)
  if (tag === "LEM") out[26] = { topic: "LEM.W", payload: watts };
  else /* REM */          out[27] = { topic: "REM.W", payload: watts };
  return out;
}

// ---------- PIDs: pass-through full 15-register variables block (addr 1..15) ----------
if (tag === "LPID" || tag === "RPID") {
  if (regs.length < 15) { node.error(`${tag} requires 15 registers (addr 1..15)`, msg); return null; }
  const block = regs.slice(0, 15); // keep raw words; decode downstream

  if (tag === "LPID") out[28] = { topic: "LPID.block", payload: block };
  else out[29] = { topic: "RPID.block", payload: block };
  return out;
}

// ---------- VFDs (6 regs pass-through to your splitter nodes) ----------
if (tag === "LVFD" || tag === "RVFD") {
  if (regs.length < 6) { node.error(`${tag} requires 6 registers`, msg); return null; }
  const arr6 = regs.slice(0, 6);
  if (tag === "LVFD") out[30] = { topic: "LVFD", payload: arr6 };
  else out[31] = { topic: "RVFD", payload: arr6 };
  return out;
}

node.warn(`Decoder: unhandled tag "${tag}"`);
return null;

// Store the current slider value in flow.leftSliderValue
flow.set("leftSliderValue", msg.payload);

// Optional warning for debugging
node.warn("Slider left value stored in flow context: " + msg.payload);

