let buf = Buffer.alloc(4);

// Write second register first (high word), then first register (low word)
buf.writeUInt16BE(reg1, 0);
buf.writeUInt16BE(reg0, 2);

// Now read as 32-bit float
let value = buf.readFloatBE(0);

// Trim to 3 decimal places and convert back to number
msg.payload = Number(value.toFixed(2));
return msg;

