    //{ tag: "LEE872P", unitid: 238, fc: 4, address: 0x4B0, quantity: 2 },
    //{ tag: "LEE872T", unitid: 238, fc: 4, address: 0x3EA, quantity: 2 },
    //{ tag: "LEE872RH", unitid: 238, fc: 4, address: 0x3FC, quantity: 2 },
