    { tag: "TagX", unitid: 1, fc: 3, address: 2057, quantity: 6 },
    { tag: "TagX2", unitid: 2, fc: 3, address: 2057, quantity: 6 },
    { tag: "Tag5", unitid: 44, fc: 4, address: 0x300, quantity: 2 }
    //{ tag: "Tag6", unitid: 44, fc: 4, address: 0x1009, quantity: 2 }
]);

global.set("index", 0);

node.warn("Global variables initialized");
return msg;

//msg.payload = global.get("addrInfo")[global.get("index")];
//msg.topic = global.get("addrInfo")[global.get("index")].tag;
//global.set("index", global.get("index") == 2 ? 0 : global.get("index") + 1);
//return msg;

let addrInfo = global.get("addrInfo");
let index = global.get("index") || 0;

// Safety check
if (!Array.isArray(addrInfo) || addrInfo.length === 0) {
    node.error("addrInfo is not set or empty", msg);
    return null;
}

msg.payload = addrInfo[index];
msg.topic = addrInfo[index].tag;

// Increment index with wrap-around
global.set("index", (index + 1) % addrInfo.length);

return msg;

// Inputs:
// msg.payload = { relay: N, state: "ON" or "OFF", bankByte: <number> }

const relayNum = msg.payload.relay;
const state = msg.payload.state.toUpperCase();
const bankByte = msg.payload.bankByte;

if (relayNum < 1 || relayNum > 8) {
    node.error("Relay number must be 1 to 8", msg);
    return null;
}

if (typeof bankByte !== 'number') {
    node.error("bankByte must be a number", msg);
    return null;
}

const header = 0xAA;
const cmd1 = 0x03;
const cmd2 = 0xFE;

let relayByte;
if (state === "ON") {
    relayByte = 108 + (relayNum - 1);
} else if (state === "OFF") {
    relayByte = 100 + (relayNum - 1);
} else {
    node.error("State must be ON or OFF", msg);
    return null;
}

const command = [header, cmd1, cmd2, relayByte, bankByte];
const checksum = command.reduce((sum, b) => sum + b, 0) % 256;
command.push(checksum);

msg.payload = Buffer.from(command);
return msg;

let bytes = msg.payload;

if (!Buffer.isBuffer(bytes)) {
    node.error("Payload is not a Buffer", msg);
    return null;
}

if (bytes.length !== 4) {
    node.error("Invalid response length", msg);
    return null;
}

const header1 = bytes[0];
const header2 = bytes[1];
const dataByte = bytes[2];
const checksum = bytes[3];

// Validate headers
if (header1 !== 0xAA || header2 !== 0x01) {
    node.error("Invalid response header", msg);
    return null;
}

// Calculate expected checksum
const calcChecksum = (header1 + header2 + dataByte) % 256;
if (calcChecksum !== checksum) {
    node.error(`Checksum mismatch. Got ${checksum}, expected ${calcChecksum}`, msg);
    return null;
}

if (checksum === 0x00) {
    // On/off response - just send null on all outputs to keep ui-leds unchanged
    return [null, null, null, null, null, null, null, null];
} else {
    // Status response - send relay states on outputs 1-8
    let msgs = [];
    for (let i = 0; i < 8; i++) {
        msgs.push({
            topic: `relay${i + 1}`,
            payload: !!((dataByte >> i) & 1)
        });
    }
    return msgs;
}

const command = [0xAA, 0x03, 0xFE, 0x7C, 0x01];
const checksum = command.reduce((sum, b) => sum + b, 0) % 256;
command.push(checksum);

msg.payload = Buffer.from(command);
return msg;

// --- Define tags ---
const swapTags = ["LEE872avg", "LEE872raw", "LEE872P", "LEE872T", "LEE872RH"];
const noSwapTags = ["tag9", "tag8", "TagX", "TagX2"];

// Merge for output order
const tags = [...swapTags, ...noSwapTags];
let outputs = tags.map(() => null);

// --- Lookup maps for per-tag math ---
const noSwapMath = {
    tag9: v => v * 10,
    tag8: v => v / 10,
};

const swapMath = {
    LEE872avg: v => v / 2,
    LEE872raw: v => v / 3,
    LEE872P: v => v / 5,
    LEE872T: v => v,   // identity
    LEE872RH: v => v,   // identity
};

// --- Helpers ---
function readInt32BE(regs) {
    const buf = Buffer.alloc(4);
    buf.writeUInt16BE(regs[0], 0);
    buf.writeUInt16BE(regs[1], 2);
    return buf.readInt32BE(0);
}

function readFloat32Swap(regs) {
    const buf = Buffer.alloc(4);
    buf.writeUInt16BE(regs[1], 0);
    buf.writeUInt16BE(regs[0], 2);
    return buf.readFloatBE(0);
}

// --- Main processing ---
let regs = msg.payload;
if (!Array.isArray(regs)) {
    node.error("Payload is not an array", msg);
    return null;
}

let value;

if (noSwapTags.includes(msg.topic)) {
    if (msg.topic === "TagX" || msg.topic === "TagX2") {
        if (regs.length < 6) {
            node.error(`${msg.topic} requires 6 registers`, msg);
            return null;
        }
        value = regs.slice(0, 6);   // keep as array
        node.warn(`${msg.topic} value handled as array of 6 registers`);
    } else {
        value = readInt32BE(regs);
        if (noSwapMath[msg.topic]) value = noSwapMath[msg.topic](value);
    }
}
else if (swapTags.includes(msg.topic)) {
    value = readFloat32Swap(regs);
    if (swapMath[msg.topic]) value = swapMath[msg.topic](value);
}
else {
    node.warn(`Unknown tag/topic: ${msg.topic}`);
    return null;
}

// --- Assign output ---
const index = tags.indexOf(msg.topic);
if (index === -1) {
    node.warn(`Unknown tag/topic: ${msg.topic}`);
    return null;
}

msg.payload = value;
outputs[index] = msg;

return outputs;

// Store the current slider value in flow.leftSliderValue
flow.set("leftSliderValue", msg.payload);

// Optional warning for debugging
node.warn("Slider left value stored in flow context: " + msg.payload);

// Do not send anything downstream
return null;

// Store the current slider value in flow.leftSliderValue
flow.set("rightSliderValue", msg.payload);

// Optional warning for debugging
node.warn("Slider Right value stored in flow context: " + msg.payload);

// Do not send anything downstream
return null;

if (msg.payload && msg.payload.address !== undefined && msg.payload.unitid !== undefined) {

    let val;

    // If value is provided in the payload, use it
    if (msg.payload.value !== undefined) {
        val = Number(msg.payload.value);
    }
    // Otherwise, get it from the stored slider value
    else {
        val = flow.get("leftSliderValue");
        if (val === undefined || isNaN(val)) {
            node.warn("Slider value not set or invalid, defaulting to 0");
            val = 0;
        }
        val = Number(val) * 100;
    }

    // Build Modbus Flex Write payload
    msg.payload = {
        fc: 6,                               // Write Single Register
        unitid: Number(msg.payload.unitid),  // From button
        address: Number(msg.payload.address),// From button
        quantity: 1,
        value: [val]                         // From slider or fixed button value
    };

    return msg;
}

// Ignore other messages
return null;

if (msg.payload && msg.payload.address !== undefined && msg.payload.unitid !== undefined) {

    let val;

    // If value is provided in the payload, use it
    if (msg.payload.value !== undefined) {
        val = Number(msg.payload.value);
    }
    // Otherwise, get it from the stored slider value
    else {
        val = flow.get("rightSliderValue");
        if (val === undefined || isNaN(val)) {
            node.warn("Slider value not set or invalid, defaulting to 0");
            val = 0;
        }
        val = Number(val) * 100;
    }

    // Build Modbus Flex Write payload
    msg.payload = {
        fc: 6,                               // Write Single Register
        unitid: Number(msg.payload.unitid),  // From button
        address: Number(msg.payload.address),// From button
        quantity: 1,
        value: [val]                         // From slider or fixed button value
    };

    return msg;
}

// Ignore other messages
return null;

// --- Expect input: msg.payload = [f, p, i, v, extra1, motorLed]
if (!Array.isArray(msg.payload) || msg.payload.length < 6) {
    node.error("Expected an array of at least 6 values (TagX/TagX2)", msg);
    return null;
}

// Destructure 6 values
const [frequency, power, current, voltage, extra1, motorLedRaw] = msg.payload;

// Scale & send each value to its own output
return [
    { payload: frequency / 100 }, // Output 1 - Frequency
    { payload: power / 100 },     // Output 2 - Power
    { payload: current / 100 },   // Output 3 - Current
    { payload: voltage / 10 },    // Output 4 - Voltage
    { payload: extra1 },          // Output 5 - Extra value (raw or scale if needed)
    { payload: motorLedRaw !== 0 } // Output 6 - MotorLed (boolean)
];

// --- Expect input: msg.payload = [f, p, i, v, extra1, motorLed]
if (!Array.isArray(msg.payload) || msg.payload.length < 6) {
    node.error("Expected an array of at least 6 values (TagX/TagX2)", msg);
    return null;
}

// Destructure 6 values
const [frequency, power, current, voltage, extra1, motorLedRaw] = msg.payload;

// Scale & send each value to its own output
return [
    { payload: frequency / 100 }, // Output 1 - Frequency
    { payload: power / 100 },     // Output 2 - Power
    { payload: current / 100 },   // Output 3 - Current
    { payload: voltage / 10 },    // Output 4 - Voltage
    { payload: extra1 },          // Output 5 - Extra value (raw or scale if needed)
    { payload: motorLedRaw !== 0 } // Output 6 - MotorLed (boolean)
];
