let addrInfo = global.get("addrInfo");
let index = global.get("index") || 0;

// Safety check
if (!Array.isArray(addrInfo) || addrInfo.length === 0) {
    node.error("addrInfo is not set or empty", msg);
    return null;
}

msg.payload = addrInfo[index];
msg.topic = addrInfo[index].tag;

// Increment index with wrap-around
global.set("index", (index + 1) % addrInfo.length);

return msg;

// Inputs:
// msg.payload = { relay: N, state: "ON" or "OFF", bankByte: <number> }

const relayNum = msg.payload.relay;
const state = msg.payload.state.toUpperCase();
const bankByte = msg.payload.bankByte;

if (relayNum < 1 || relayNum > 8) {
    node.error("Relay number must be 1 to 8", msg);
    return null;
}

if (typeof bankByte !== 'number') {
    node.error("bankByte must be a number", msg);
    return null;
}

const header = 0xAA;
const cmd1 = 0x03;
const cmd2 = 0xFE;

let relayByte;
if (state === "ON") {
    relayByte = 108 + (relayNum - 1);
} else if (state === "OFF") {
    relayByte = 100 + (relayNum - 1);
} else {
    node.error("State must be ON or OFF", msg);
    return null;
}

const command = [header, cmd1, cmd2, relayByte, bankByte];
const checksum = command.reduce((sum, b) => sum + b, 0) % 256;
command.push(checksum);

msg.payload = Buffer.from(command);
return msg;

let bytes = msg.payload;

if (!Buffer.isBuffer(bytes)) {
    node.error("Payload is not a Buffer", msg);
    return null;
}

if (bytes.length !== 4) {
    node.error("Invalid response length", msg);
    return null;
}

const header1 = bytes[0];
const header2 = bytes[1];
const dataByte = bytes[2];
const checksum = bytes[3];

// Validate headers
if (header1 !== 0xAA || header2 !== 0x01) {
    node.error("Invalid response header", msg);
    return null;
}

// Calculate expected checksum
const calcChecksum = (header1 + header2 + dataByte) % 256;
if (calcChecksum !== checksum) {
    node.error(`Checksum mismatch. Got ${checksum}, expected ${calcChecksum}`, msg);
    return null;
}

if (checksum === 0x00) {
    // On/off response - just send null on all outputs to keep ui-leds unchanged
    return [null, null, null, null, null, null, null, null];
} else {
    // Status response - send relay states on outputs 1-8
    let msgs = [];
    for (let i = 0; i < 8; i++) {
        msgs.push({
            topic: `relay${i + 1}`,
            payload: !!((dataByte >> i) & 1)
        });
    }
    return msgs;
}

const command = [0xAA, 0x03, 0xFE, 0x7C, 0x01];
const checksum = command.reduce((sum, b) => sum + b, 0) % 256;
command.push(checksum);

msg.payload = Buffer.from(command);
return msg;

// Define tags
const swapTags = ["LEE872avg", "LEE872raw", "LEE872P", "LEE872T", "LEE872RH"];
const noSwapTags = ["tag9", "tag8"];

// Merge for output order
const tags = [...swapTags, ...noSwapTags];
let outputs = tags.map(() => null);

// Get registers (supports both Modbus formats: array or {data:[]})
let registers = Array.isArray(msg.payload) ? msg.payload : msg.payload.data;

if (!Array.isArray(registers)) {
    node.error("Payload is not an array", msg);
    return null;
}
if (registers.length < 2) {
    node.error("Register array too short", msg);
    return null;
}

let reg0 = registers[0];
let reg1 = registers[1];

if (typeof reg0 !== "number" || typeof reg1 !== "number") {
    node.error("Register values are not numbers", msg);
    return null;
}

// Buffer handling
let buf = Buffer.alloc(4);
if (noSwapTags.includes(msg.topic)) {
    // No swap
    buf.writeUInt16BE(reg0, 0);
    buf.writeUInt16BE(reg1, 2);
} else if (swapTags.includes(msg.topic)) {
    // Swap
    buf.writeUInt16BE(reg1, 0);
    buf.writeUInt16BE(reg0, 2);
} else {
    node.warn(`Unknown tag/topic: ${msg.topic}`);
    return null;
}

let value = buf.readFloatBE(0);

// Apply per-tag math
let adjustedValue;
switch (msg.topic) {
    case "LEE872avg":
        adjustedValue = value / 1;
        break;
    case "LEE872raw":
        adjustedValue = value / 3;
        break;
    case "LEE872P":
        adjustedValue = value / 5;
        break;
    case "LEE872T":
        adjustedValue = value;
        break;
    case "LEE872RH":
        adjustedValue = value / 10;
        break;
    case "tag9":
        adjustedValue = value * 10; // Example no-swap
        break;
    case "tag8":
        adjustedValue = value - 100; // Example no-swap
        break;
    default:
        adjustedValue = value;
}

// Send to correct output
const index = tags.indexOf(msg.topic);
if (index === -1) {
    node.warn(`Unknown tag/topic: ${msg.topic}`);
    return null;
}

msg.payload = Number(adjustedValue.toFixed(2));
outputs[index] = msg;
return outputs;



